import express from "express";
import { getNearbyBusStands, getBusRoutes, getLiveBusLocation, getAvailableBuses } from "../controllers/bus.controller";

const busRouter = express.Router();

busRouter.get("/nearby-stops", getNearbyBusStands);
busRouter.get("/routes", getBusRoutes);
busRouter.get("/routes/:routeId/live", getLiveBusLocation);

// New: list available buses between from/to
busRouter.get("/available", getAvailableBuses);

export default busRouter;
